import { assert, expect, should } from 'chai';

import web3 from '../../web3';

describe("Tolar net", function () {
    let _web3: any = new web3();
    _web3.setProvider("https://tolar.dream-factory.hr");
    let tolar = _web3.tolar;
    let net = tolar.net;
    it("peerCount", async () => {
        let peerCount = await net.peerCount();
        expect(peerCount).to.be.a("number");
    }).timeout(10000);
    it("masterNodeCount", async () => {
        let masterNodeCount = await net.masterNodeCount();
        expect(masterNodeCount).to.be.a("number");
    }).timeout(10000);
    it("isMasterNode", async () => {
        let isMasterNode = await net.isMasterNode();
        expect(isMasterNode).to.be.a("boolean");
    }).timeout(10000);
    it("maxPeerCount", async () => {
        let maxPeerCount = await net.maxPeerCount();
        expect(maxPeerCount).to.be.a("number");
    }).timeout(10000);
});
